import React, { useRef } from 'react';

const UploadButton = ({ children, onChange }) => {
  const inputEl = useRef(null);
  return (
    <label>
      <input
        type="file"
        className="upload-button-file"
        ref={inputEl}
        onChange={() => onChange(inputEl)}
      />
      {children}
    </label>
  );
};

export default UploadButton;
