import React, { useRef } from 'react';

const UploadButton = ({ children, onChange }) => {
    const inputEl = useRef(null);
    return (
        <label className="bg-blue-700 font-light px-12 py-3 text-white inline-block w-full md:w-auto md:m-0 mb-6">
            <input type="file" className="upload-button-file" ref={inputEl} onChange={() => onChange(inputEl)} />
            {children}
        </label>
    );
}

export default UploadButton;
