import React, { useState, useEffect } from 'react';
import { SearchInput, UploadButton, DocumentCard } from './components';

const formatBytes = (bytes, decimals = 2) => {
  if (bytes === 0) return '0 Bytes';

  const k = 1024;
  const dm = decimals < 0 ? 0 : decimals;
  const sizes = ['bytes', 'kb', 'mb'];

  const i = Math.floor(Math.log(bytes) / Math.log(k));

  return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
}

const documents = Array(6)
  .fill(null)
  .map((_, i) => ({
    id: i + 1,
    name: `Doc${i + 1}`,
    size: '100kb'
  }));

const App = () => {
  const [searchInput, setSearchInput] = useState('');
  const totalSize = `${documents.reduce(
    (total, doc) => total + parseInt(doc.size.slice(0, -2), 10),
    0
  )}kb`;

  useEffect(() => {
    fetch('http://localhost:8000/documents')
      .then(response => response.json())
      .then(response => console.log('response', response));
    return () => {}; 
  }, []);

  const onUploadChange = item => {
    const formData = new FormData();
    const { files } = item.current;
    console.log('files', files, files.length);
    for (let i = 0; i < files.length; i++) {
      formData.append('file', files[i]);
    }
    console.log('form data', formData);
    fetch('http://localhost:8000/documents', { method: 'POST', body: formData })
      .then(response => response.json())
      .then(response => console.log('response', response));
  };

  return (
    <div className="mx-auto" style={{ maxWidth: 960 }}>
      <header className="mx-4 md:mx-0 my-8 flex md:flex-row flex-col-reverse items-center justify-between">
        <SearchInput
          value={searchInput}
          onChange={e => setSearchInput(e.target.value)}
        />
        <UploadButton onChange={onUploadChange}>Upload</UploadButton>
      </header>
      <main className="m-4 md:mx-0">
        <div className="flex flex-wrap items-baseline justify-between">
          <h2 className="w-full md:w-auto">{documents.length} Documents</h2>
          <div>Total size: {totalSize}</div>
        </div>
        <div className="flex flex-wrap -mx-4">
          {documents.map(doc => (
            <div key={doc.name} className="w-full md:w-1/3">
              <DocumentCard
                name={doc.name}
                size={doc.size}
                onDelete={() => console.log('delete', doc.name)}
              />
            </div>
          ))}
        </div>
      </main>
    </div>
  );
};

export default App;
